const shoes = [
    { id: 1, name: 'Sneakers', quantity: 10, price: 15000, image: 'https://encrypted-tbn3.gstatic.com/shopping?q=tbn:ANd9GcQViPslpRUL2xAePZaIN03aVIhgaxGlJDzvqdTeoRaz2Fsek4VfXz2zAIefqIX2e8gvM1qIjcPFViyBPCkk-xlRGGdoPTboAhPnm8PzVGxpb0RG_q2LE7hy_hpKnsuHFFehH95_YNlxXQ&usqp=CAc' },
    { id: 2, name: 'Boots', quantity: 8, price: 25000, image: 'data:image/jpeg;base64,' },
    { id: 3, name: 'Sandals', quantity: 15, price: 5000, image: 'data:image/jpeg;base64,' },
    { id: 4, name: 'Formal Shoes', quantity: 5, price: 2500, image: 'data:image/jpeg;base64,' }
];

let cart = [];

let button = document.getElementById('show-list');
const shoeListDiv = document.querySelector('.shoe-list');
const cartListDiv = document.querySelector('.cart-list');

button.addEventListener('click', displayShoeList);

function displayShoeList() {
    shoeListDiv.innerHTML = '<h2>Available Shoes in Stock</h2>';
    shoes.forEach(shoe => {
        const shoeDiv = document.createElement('div');
        shoeDiv.classList.add('shoe-item');
        shoeDiv.innerHTML = `
            ${shoe.name} - Quantity: ${shoe.quantity}, Price: ₦${shoe.price},  
            <img src="${shoe.image}" alt="${shoe.name}" style="width: 150px; height: 150px; object-fit: cover;" />
            <button id="add-to-cart-${shoe.id}">Add to cart</button>
        `;
        shoeListDiv.appendChild(shoeDiv);

        // Attach event listener to the Add to cart button
        document.getElementById(`add-to-cart-${shoe.id}`).addEventListener('click', () => addToCart(shoe.id));
    });
    updateCartList();
}

function addToCart(shoeId) {
    console.log("Adding to cart:", shoeId); // Debugging line
    const shoe = shoes.find(s => s.id === shoeId);
    if (shoe && shoe.quantity > 0) {
        const cartItem = cart.find(item => item.id === shoeId);
        if (cartItem) {
            cartItem.quantity += 1;
        } else {
            cart.push({ id: shoe.id, name: shoe.name, quantity: 1, price: shoe.price });
        }
        shoe.quantity -= 1;
        updateCartList();
        displayShoeList();
    } else {
        alert('Item out of stock');
    }
}

function removeFromCart(shoeId) {
    const cartItem = cart.find(item => item.id === shoeId);
    if (cartItem) {
        const shoe = shoes.find(s => s.id === shoeId);
        shoe.quantity += 1;
        cartItem.quantity -= 1;
        if (cartItem.quantity === 0) {
            cart = cart.filter(item => item.id !== shoeId);
        }
        updateCartList();
        displayShoeList();
    }
}

function updateCartList() {
    cartListDiv.innerHTML = '<h2>My Cart List</h2>';
    let totalItems = 0;
    let totalPrice = 0;
    cart.forEach(item => {
        totalItems += item.quantity;
        totalPrice += item.quantity * item.price;
        const cartDiv = document.createElement('div');
        cartDiv.classList.add('cart-item');
        cartDiv.innerHTML = `
            ${item.name} - Quantity: ${item.quantity}, Price: ₦${item.price * item.quantity}
            <button onclick="removeFromCart(${item.id})">Remove from cart</button>
        `;
        cartListDiv.appendChild(cartDiv);
    });
    document.getElementById('cart-counter').textContent = totalItems;
    cartListDiv.innerHTML += `<h3>Total Price: ₦${totalPrice}</h3>`; // Fixed the backtick issue
}

// Automatically display shoe list when page loads
window.onload = () => {
    displayShoeList();
};
